INSERT INTO `t_evaluation` VALUES (1, 388, 204, 'JQuery easyUi datagrid 中 使用datagrid生成数据列表后，需要在每一行加入一个操作按钮列，按钮在默认非编辑状态下是不显示的，需要激活行编辑状态下才显示，故不能再formatter处理，需要使用自定义按钮来处理了。\r\n', NULL, '2014-8-22 15:44:27', '2014-8-22 15:44:27', 52, 0);
INSERT INTO `t_evaluation` VALUES (2, 388, 204, '组合框（combobox）显示一个可编辑的文本框和下拉列表，用户可以从下拉列表中选择一个或多个值。用户可以直接输入文本到列表的顶部，或者从列表中选择一个或多个现成的值。', NULL, '2014-8-22 16:04:50', '2014-8-22 16:04:51', 52, 0);
